import { Component, Renderer2 } from '@angular/core';

@Component({
  selector: 'app-cabecalho',
  templateUrl: './cabecalho.component.html',
  styleUrls: ['./cabecalho.component.css']
})
export class CabecalhoComponent{

  constructor(private renderer: Renderer2) {}

  ngAfterViewInit() {
    // Ensure that the script is loaded after the view is initialized
    const script = document.createElement('script');
    script.src = 'assets/navbar.js';
    script.async = true;
    document.body.appendChild(script);
  }
}
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.4/css/all.min.css">

<div class="container navbar">
  <button class="navbar-toggle" (click)="toggleNavbar()">☰ Menu</button>
  <div class="navbar-links" id="navbar-links">
    <ul>
      <li><a routerLink="/" routerLinkActive="active">Inicios</a></li>
      <li><a routerLink="/curriculo" routerLinkActive="active">Currículo</a></li>
      <li><a routerLink="/sobreMim" routerLinkActive="active">Sobre Mim</a></li>

    </ul>

    <div class="social-icons">
      <a href="https://www.instagram.com/gui.viana14/?next=%2F" class="icon-link"><i class="fab fa-instagram"></i></a>
      <a href="https://www.linkedin.com/in/guilherme-viana-filgueiras-4b5211234/" class="icon-link"><i class="fab fa-linkedin-in"></i></a>
      <a href="https://github.com/GuilhermeViana14" class="icon-link"><i class="fab fa-github"></i></a>
    </div>
  </div>
</div>

<router-outlet></router-outlet>
